import React from "react";
import '../../../../';
import banner from '../../assets/default-event-posters-image5.jpg'

const EventCard = ({ event }) => {
    const {
        name,
        event_website,
        description,
    } = event;
    return (
        <section className="banner">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-9 col-6">
                        <div className="image-wrapper">
                            <img src={banner} alt="Banner Image" className="img-fluid"></img>
                        </div>
                    </div>
                    <div className="col-lg-3 col-6">
                        <div className="event-wrapper">
                            <div className="event-card">
                                <h2>{name}</h2>
                                <p>Event Live Link:  <a href={event_website} target="_blank" rel="">Open Streaming Website</a></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
           
        </section>
    );
};

export default EventCard;
